            service.addNotes(nota);
            this.input = "";
            StateHasChanged();
            await Task.Delay(1);
            cargarDB();
            StateHasChanged();
        }
    }
    void cargarDB()
    {
        listado.Clear();
        listado = service.getNotes();
    }

    void deleteButtom(int id)
    {
        service.removeNotes(id);
        cargarDB();
        StateHasChanged();
    }

    void completar(Notas nota, bool completa)
    {
        nota.completada = completa;
        service.getEditNotes(nota);
        cargarDB();
    }
